import React, { useState, useEffect } from "react";
import useHttp from "../hooks/use-http";

const Banner = () => {
  const [movie, setMovie] = useState("");
  const [randomMovie, setRandomMovie] = useState([]);
  const {
    isLoading: isLoading,
    error: error,
    sendRequest: fetchMovies,
  } = useHttp();

  useEffect(() => {
    const transformMovies = (movieObj) => {
      console.log(movieObj);
      setRandomMovie(movieObj.results);
    };

    fetchMovies(
      {
        url: "https://api.themoviedb.org/3/discover/tv?api_key=2a15e12781b1443c104dcdef44b9102d&with_network=123",
      },
      transformMovies
    );
  }, [fetchMovies]);

  // const moviesList = randomMovie.map((movie) =>
  //   console.log(movie.backdrop_path)
  // );
  const moviesList = [...randomMovie];

  return (
    <div>
      <p>Banner</p>
      <h1>{moviesList.length === 20 ? "HEHE" : "HAHA"}</h1>
    </div>
  );
};

export default Banner;
