import React, { useState, useEffect } from "react";
import useHttp from "../hooks/use-http";

const Banner = () => {
  const [movies, setMovies] = useState("");
  const [randomMovie, setRandomMovie] = useState([]);
  const [backdrop, setBackdrop] = useState("");
  const {
    isLoading: isLoading,
    error: error,
    sendRequest: fetchMovies,
  } = useHttp();

  const src1 = useEffect(() => {
    const transformMovies = (movieObj) => {
      console.log(movieObj);
      setMovies(movieObj.results);
      setRandomMovie(movies[Math.floor(Math.random() * movies.length - 1)]);
      setBackdrop(randomMovie.backdrop_path);
    };

    fetchMovies(
      {
        url: "https://api.themoviedb.org/3/discover/tv?api_key=2a15e12781b1443c104dcdef44b9102d&with_network=123",
      },
      transformMovies
    );
  }, [fetchMovies]);

  const movie = [...movies];
  console.log(movie);
  const randomMovie1 = movie[Math.floor(Math.random() * movie.length - 1)];
  console.log(randomMovie1);
  // const src = `https://image.tmdb.org/t/p/w500${backdrop}`;

