  // console.log(moviesList);
  // const randomMovieFinal =
  //   moviesList[Math.floor(Math.random() * moviesList.length - 1)];
  // console.log(randomMovieFinal);
  // setBackdrop(
  //   "https://image.tmdb.org/t/p/w500/5AkPhazx8F0Ht74CUdJU03vNzBi.jpg"
  // );

  // const src = `"https://image.tmdb.org/t/p/w500${randomMovieFinal.backdrop_path}"`;
  // /218ZehBKlH8efPRRccmB7bu0oLQ.jpg
  return (
    <div className="background-img">
      <p>Banner</p>
      {/* <h1>{moviesList.length === 20 ? "HEHE" : "HAHA"}</h1> */}
      <img src={src1}></img>
    </div>
  );
};

export default Banner;
