import React, { useState } from "react";

const NavBar = () => {
  const [scroll, setScroll] = useState(false);
  if (window.scrollY > 100) {
    setScroll(true);
  } else {
    setScroll(false);
  }
  // const navbarClasses = (window.scrollY > 100px) ? "navbar navbar-bgr" :"navbar navbar-transparent"
  return (
    <div className="navbar">
      <h1>Movie App</h1>
      <h1>Icon</h1>
    </div>
  );
};

export default NavBar;
